<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Store extends CI_Controller {		function __construct() {		parent::__construct();		$this->template->title('Welcome to CEGHypermarket')				->set('currentSection', 'store_index')				->set_layout('default');	}			function store1()	{		$session_data = array(		    'store_id'   => 1		);				$this->session->set_userdata($session_data);		$this->template->build('store_index');	}		function store2()	{		$session_data = array(		    'store_id'   => 2		);		$this->session->set_userdata($session_data);				$this->template->build('store_index');	}		function store3()	{		$session_data = array(		    'store_id'   => 3		);		$this->session->set_userdata($session_data);		$this->template->build('store_index');	}		function store4()	{		$session_data = array(		    'store_id'   => 4		);		$this->session->set_userdata($session_data);		$this->template->build('store_index');	}		function store5()	{		$session_data = array(		    'store_id'   => 5		);		$this->session->set_userdata($session_data);		$this->template->build('store_index');	}}